import { React, useContext, useState} from 'react';
import Nav from '../Nav/nav';
import {FnameContext, LnameContext} from '../contextFile/contextFile';
import './style.css';


function Settings(){

    const stgmsg = 'This is the "Settings" Page'
    const firstname = useFormInput('firstname','Midhuna','');
    const lastname = useFormInput('lastname','M','');
    const {setFirstName} = useContext(FnameContext);
    const {setLastName} = useContext(LnameContext);

    return(
        <div className="row">
            <Nav/>
            <main>
                <div className="outerContainer">
                <p>{stgmsg}</p>
                <form>
                    <div className='formData'>
                    <div className='formData1'>
                    <label>
                        FirstName : 
                    </label>
                    <input type="text"  placeholder="firstname" {...firstname}/>
                    <br/>
                    <br/>
                    <label>
                        LastName : 
                    </label>
                    <input type="text" placeholder="lastname" {...lastname}/>
                    </div>
                    </div>
                    <br/><br/>
                    <button type="button" onClick={()=> {
                        setFirstName(firstname.value); 
                        setLastName(lastname.value)
                        }}> Update </button>
                </form>
                </div>
            </main>
        </div>
    )
}

const useFormInput = (fieldName, initialValue, setError) => {
    const [value, setValue] = useState({"fieldName":fieldName, "value":initialValue, "err_msg":""});

    const handleChange = e => {
      var new_value = {"fieldName":value.fieldName, "value": e.target.value, "err_msg": ""};
      setValue(new_value);
      console.log(JSON.stringify(new_value));
    }
    return {
      value: value.value,
      onChange: handleChange
    }
}  

export default Settings;
