import { React, useContext, useState} from 'react';
import Nav from '../Nav/nav';
import {FnameContext, LnameContext} from '../contextFile/contextFile'


function Settings(props){

    const stgmsg = 'This is the "Settings" Page'
    const firstname = useFormInput('firstname','','');
    const lastname = useFormInput('lastname','','');
    const {firstName, setFirstName} = useContext(FnameContext);
    const {lastName, setLastName} = useContext(LnameContext);

    return(
        <div className="row">
            <Nav/>
            <main>
                <p>{stgmsg}</p>
                <form>
                    <label>
                        FirstName : 
                    </label>
                    <input type="text" placeholder="firstname" {...firstname}/>
                    <br/>
                    <br/>
                    <label>
                        LastName : 
                    </label>
                    <input type="text" placeholder="lastname" {...lastname}/>
                    <br/><br/>
                    <button type="button" onClick={()=> {
                        setFirstName(firstname.value); 
                        setLastName(lastname.value)
                        }}> Update </button>
                </form>
                <p>{firstName}</p>
                <p>{lastName}</p>
            </main>
        </div>
    )
}

const useFormInput = (fieldName, initialValue, setError) => {
    const [value, setValue] = useState({"fieldName":fieldName, "value":initialValue, "err_msg":""});

    const handleChange = e => {
      var new_value = {"fieldName":value.fieldName, "value": e.target.value, "err_msg": ""};
      setValue(new_value);
      console.log(JSON.stringify(new_value));
    }
    return {
      value: value.value,
      onChange: handleChange,
      onBlue: handleChange
    }
}  

export default Settings;
