const useFormInput = (fieldName, initialValue, setError) => {
    const [value, setValue] = useState({"fieldName":fieldName, "value":initialValue, "err_msg":""});

    const handleChange = e => {
      var new_value = {"fieldName":value.fieldName, "value": e.target.value, "err_msg": ""};
      setValue(new_value);
      setError(false);
      console.log(JSON.stringify(new_value));
    }
    return {
      value: value.value,
      onChange: handleChange,
      onBlue: handleChange
    }
}  

export default Settings;
