import { React, useState} from 'react';
import Nav from './Nav/nav';
import Home from '../home/home';

export const ThemeContext = React.createContext();

function Settings(props){

    const stgmsg = 'This is the "Settings" Page'
    const [error,setError] = useState(null);
    const firstname = useFormInput('firstname','',setError);
    const lastname = useFormInput('lastname','',setError);

    return(
        <div className="row">
            <ThemeContext.Provider value={firstname.value}>
            <Nav/>
            <main>
                <p>{stgmsg}</p>
                <form>
                    <label>
                        FirstName : 
                    </label>
                    <input type="text" placeholder="firstname" {...firstname}/>
                    <br/>
                    <br/>
                    <label>
                        LastName : 
                    </label>
                    <input type="text" placeholder="lastname" {...lastname}/>
                    <br/><br/>
                    <button type="button">Update</button>
                </form>
            </main>
            <Home/>
            <Products/>
            </ThemeContext.Provider>
        </div>
    )
}

const useFormInput = (fieldName, initialValue, setError) => {
    const [value, setValue] = useState({"fieldName":fieldName, "value":initialValue, "err_msg":""});

    const handleChange = e => {
      var new_value = {"fieldName":value.fieldName, "value": e.target.value, "err_msg": ""};
      setValue(new_value);
      setError(false);
      console.log(JSON.stringify(new_value));
    }
    return {
      value: value.value,
      onChange: handleChange,
      onBlue: handleChange
    }
}  

export default Settings;
